import{_ as p}from"./plugin-vue_export-helper.21dcd24c.js";import{o as e,c as a,e as r}from"./app.13813300.js";const t={},h=r('<h1 id="\u98DB\u51E1\u7684\u6BB5\u843D\u5206\u6790" tabindex="-1"><a class="header-anchor" href="#\u98DB\u51E1\u7684\u6BB5\u843D\u5206\u6790" aria-hidden="true">#</a> \u98DB\u51E1\u7684\u6BB5\u843D\u5206\u6790</h1><p>\u5831\u544A \u7B2C\u4E8C\u90E8\u5206 \u91CD\u9EDE\u6BB5\u843D\u5206\u6790</p><p>\u7B2C\u4E8C\u90E8\u5206\u532F\u5831\u7684\u4E3B\u984C\uFF1A</p><p>\u4EFB\u52D9\uFF1A\u5B8C\u6210\u8ECD\u5730\u5404\u7D1A\u6307\u63EE\u6A5F\u69CB\u7D44\u5EFA\u3002</p><p>\u6642\u9593\u7BC0\u9EDE\uFF1A\u5EE3\u6771\u7701\u57284\u670818\u65E518\u6642</p><p>\u7B2C\u4E8C\u90E8\u5206\u532F\u5831\u7684\u91CD\u9EDE\u5167\u5BB9\uFF1A</p><p>\u4E3B\u984C\uFF1A\u7701\u7D1A\u8ECD\u5730\u6307\u63EE\u6A5F\u69CB\u7684\u7D44\u7E54\u67B6\u69CB</p><img src="https://sat02pap002files.storage.live.com/y4mekRegkvlrsDT-FVMuPq7UbIndzMYcfHmNbxPwruFYLTjCP8uq5t6ldd6HXg6bKuIiRyuOZkBzygFk7TzLGYtCRuBCa-647FQCyHX-Q649P2bIPxoCYaZG34xu0G3gqzzuZFcTbvPEYk765Xx1g5JOflnVvxdmkGOIbl8MuFQql229MrMKcmzEUcjmJuOo-bY?width=2500&amp;height=1195&amp;cropmode=none" width="100%" height="100%"><p>\u5167\u5BB9\uFF1A\u7701\u7D1A\u8ECD\u5730\u6307\u63EE\u6A5F\u69CB\u5404\u7D44\u6210\u90E8\u9580\u7684\u540D\u7A31\u3001\u8077\u80FD\u3001\u4EBA\u54E1\u3001\u5206\u5DE5\u3001\u5DE5\u4F5C\u90E8\u7F72\u53CA\u4FE1\u606F\u50B3\u905E\u901A\u8DEF</p><h2 id="\u7701\u7D1A\u8ECD\u5730\u6307\u63EE\u6A5F\u69CB\u5404\u90E8\u9580\u7684\u540D\u7A31" tabindex="-1"><a class="header-anchor" href="#\u7701\u7D1A\u8ECD\u5730\u6307\u63EE\u6A5F\u69CB\u5404\u90E8\u9580\u7684\u540D\u7A31" aria-hidden="true">#</a> \u7701\u7D1A\u8ECD\u5730\u6307\u63EE\u6A5F\u69CB\u5404\u90E8\u9580\u7684\u540D\u7A31</h2><p>\u4E00\u3001\u7701\u7D1A\u8ECD\u5730\u6307\u63EE\u6A5F\u69CB\u5404\u90E8\u9580\u7684\u540D\u7A31\uFF1A\u7701\u8ECD\u5730\u806F\u5408\u6307\u63EE\u90E8\u3001\u5357\u6D77\u65B9\u5411\u6307\u63EE\u6240\u3001\u548C\u82E5\u5E79\u5C08\u9805\u52D5\u54E1\u6307\u63EE\u6240\u7B49\u3002</p><p>1.1\u3001\u7701\u8ECD\u5730\u806F\u5408\u6307\u63EE\u90E8\u7684\u55AE\u4F4D\u69CB\u6210\uFF1A\u7531\u7701\u59D4\uFF0C\u7701\u653F\u5E9C\uFF0C\u7701\u8ECD\u5340\u548C\u7701\u570B\u52D5\u59D4\u76F8\u95DC\u6210\u54E1\u4F01\u696D\u55AE\u4F4D\uFF0C\u4EE5\u53CA\u914D\u5C6C\u7684\u6B66\u8B66\u5EE3\u6771\u7E3D\u968A\uFF0C\u6B66\u8B66\u6A5F\u52D5\u516D\u652F\u968A\uFF0C\u6D77\u9632311\u65C5\u7CBE\u5E79\u4EBA\u54E1\u7DE8\u6210\uFF0C\u662F\u7701\u8ECD\u5340\u4F5C\u6230\u6307\u63EE\u4E2D\u5FC3\uFF0C\u540C\u6642\u4E5F\u662F\u7701\u6230\u5099\u6307\u63EE\u4E2D\u5FC3\u3002</p><p>1.2\u3001\u7701\u7D1A\u8ECD\u5730\u6307\u63EE\u90E8\u7684\u5DE5\u4F5C\u8077\u80FD\uFF1A\u4F5C\u70BA\u672C\u8F44\u5340\u6307\u63EE\u904B\u7528\u570B\u9632\u52D5\u54E1\u8CC7\u6E90\uFF0C\u7D44\u7E54\u52D5\u54E1\u843D\u5BE6\u7684\u6700\u9AD8\u6C7A\u7B56\u6307\u63EE\u6A5F\u95DC\uFF0C\u76F4\u63A5\u63A5\u53D7\u6771\u90E8\u3001\u5357\u90E8\u6230\u5340\u6307\u63EE\uFF0C\u91CD\u9EDE\u7C4C\u5283\u7D44\u7E54\u6230\u7565\u6C7A\u7B56\u548C\u570B\u9632\u52D5\u54E1\u884C\u52D5\uFF0C\u4EE5\u53CA\u5168\u7701\u9632\u885B\u4F5C\u6230\u884C\u52D5\u3002</p><p>1.3\u3001\u7701\u7D1A\u8ECD\u5730\u6307\u63EE\u90E8\u7684\u4EBA\u54E1\u7D44\u6210\uFF1A\u7531\u7701\u59D4\u66F8\u8A18\u70BA\u7E3D\u6307\u63EE\u54E1\uFF0C\u7701\u9577\uFF0C\u7701\u8ECD\u5340\u53F8\u4EE4\u54E1\uFF0C\u653F\u6CBB\u59D4\u54E1\u70BA\u6307\u63EE\u54E1\u3002</p><p>1.4\u3001\u7701\u7D1A\u8ECD\u5730\u6307\u63EE\u90E8\u5206\u70BA\u5169\u500B\u4E2D\u5FC3\uFF1A\u6307\u63EE\u63A7\u88FD\u548C\u570B\u9632\u52D5\u54E1\u5169\u500B\u4E2D\u5FC3\uFF0C\u5169\u500B\u4E2D\u5FC3\u5206\u5225\u8A2D\u7F6E\u7BA1\u7406\u5354\u8ABF\u3001\u653F\u6CBB\u5DE5\u4F5C\u3001\u5F8C\u88DD\u4FDD\u969C\u7B49\u4E09\u500B\u90E8\u9580\u3002</p><p>a\u3001\u6307\u63EE\u63A7\u88FD\u4E2D\u5FC3\uFF1A\u4E3B\u8981\u8CA0\u8CAC\u4FDD\u969C\u6307\u63EE\u54E1\u5B9A\u4E0B\u53C3\u6230\u652F\u524D\u884C\u52D5\u6C7A\u5FC3\uFF0C\u6307\u63EE\u63A7\u88FD\u5730\u9762\u9632\u885B\u3001\u6D77\u4E0A\u5075\u67E5\u3001\u4EBA\u6C11\u9632\u7A7A\u3001\u5354\u52A9\u53CD\u6050\u7DAD\u7A69\u7684\u884C\u52D5\u3002</p><p>b\u3001\u570B\u9632\u52D5\u54E1\u4E2D\u5FC3\uFF1A\u4E3B\u8981\u8CA0\u8CAC\u4FDD\u969C\u6307\u63EE\u54E1\u5B9A\u4E0B\u52D5\u54E1\u6C7A\u5FC3\uFF0C\u7D44\u7E54\u5354\u8ABF\u843D\u5BE6\u570B\u9632\u52D5\u54E1\u4EFB\u52D9\uFF0C\u7D44\u7E54\u52D5\u54E1\u652F\u63F4\u3001\u96C6\u7D50\u3001\u8F38\u9001\u3001\u4EA4\u63A5\u7B49\u884C\u52D5\uFF1B\u5176\u4ED6\u4E09\u500B\u90E8\u9580\u5206\u5225\u8CA0\u8CAC\u76F8\u95DC\u9818\u57DF\u7684\u7C4C\u5283\u7D44\u7E54\u5354\u8ABF\u5DE5\u4F5C\u3002</p><p>1.5\u3001\u7701\u7D1A\u8ECD\u5730\u806F\u5408\u6307\u63EE\u90E8\u7684\u6C7A\u7B56\u548C\u904B\u884C\u6A5F\u88FD\uFF1A\u5EFA\u7ACB\u7D71\u4E00\u9818\u5C0E\uFF0C\u5206\u57DF\u6C7A\u7B56\uFF0C\u5354\u8ABF\u843D\u5BE6\u7684\u6307\u63EE\u6C7A\u7B56\u8207\u904B\u884C\u6A21\u5F0F\u3002</p><p>1.5.1\u3001\u8A2D\u7ACB\u7D71\u4E00\u9818\u5C0E\u7684\u6307\u63EE\u90E8\u9EE8\u59D4\uFF0C\u7B2C\u4E00\u66F8\u8A18\u7531\u7701\u59D4\u66F8\u8A18\u64D4\u4EFB\uFF0C\u66F8\u8A18\u548C\u526F\u66F8\u8A18\u5206\u5225\u7531\u7701\u8ECD\u5340\u653F\u6CBB\u59D4\u54E1\uFF0C\u7701\u9577\u548C\u7701\u8ECD\u5340\u53F8\u4EE4\u54E1\u64D4\u4EFB\uFF0C\u9EE8\u59D4\u96C6\u9AD4\u5C0D\u53C3\u6230\u652F\u524D \uFF0C\u548C\u570B\u9632\u52D5\u54E1\u91CD\u5927\u4E8B\u9805\u9032\u884C\u7814\u7A76\u6C7A\u7B56\u3002</p><p>1.5.2\u3001\u660E\u78BA\u5206\u57DF\u6C7A\u7B56\u7684\u5206\u5DE5\u8CA0\u8CAC\u88FD\uFF0C\u9EE8\u59D4\u4F5C\u51FA\u6C7A\u5B9A\u5F8C\uFF0C\u7531\u6307\u63EE\u54E1\u5206\u5DE5\u8CA0\u8CAC\uFF0C\u8CAB\u5FB9\u57F7\u884C\uFF0C\u7701\u9577\u8CA0\u8CAC\u7D44\u7E54\u52D5\u54E1\u4EFB\u52D9\u843D\u5BE6\uFF0C\u7701\u8ECD\u5340\u53F8\u4EE4\u54E1\u8CA0\u8CAC\u7D44\u7E54\u6307\u63EE\u53C3\u6230\u652F\u524D \u884C\u52D5\uFF0C\u5354\u540C\u7701\u9577\u7D44\u7E54\u52D5\u54E1\u4EFB\u52D9\u843D\u5BE6\uFF0C\u7701\u8ECD\u5340\u653F\u6CBB\u59D4\u54E1\u8CA0\u8CAC\u7D44\u7E54\u9818\u5C0E\u653F\u6CBB\u5DE5\u4F5C\uFF0C\u5354\u540C\u53F8\u4EE4\u54E1\u7D44\u7E54\u6307\u63EE\u6709\u95DC\u884C\u52D5\u3002</p><p>1.5.3\u3001\u532F\u805A\u5354\u540C\u843D\u5BE6\u7684\u5DE5\u4F5C\u57F7\u884C\u529B\uFF0C\u5EFA\u7ACB\u4E8B\u52D9\u5354\u8ABF\uFF0C\u806F\u5E2D\u6703\u5546\u7684\u6A5F\u88FD\uFF0C\u4E8B\u524D\u6703\u5546\u554F\u984C\u6E05\u55AE\uFF0C\u4E8B\u4E2D\u53CD\u994B\u52D5\u54E1\u9032\u7A0B\uFF0C\u4E8B\u5F8C\u806F\u8A55\u6210\u6548\u6210\u679C\uFF0C\u5BE6\u73FE\u5FEB\u6377\u9AD8\u6548\u6307\u63EE\u3002</p><p>1.5.4\u3001\u6307\u63EE\u4FDD\u969C\u9AD4\u7CFB\uFF0C\u5728\u4F9D\u6258\u6578\u64DA\u4FE1\u606F\u5BA4\u4FE1\u606F\u4FDD\u969C\u7684\u57FA\u790E\u4E0A\uFF0C\u6309\u7167\u7701\u7D1A\u4E00\u500B\u71DF\uFF0C\u5E02\u7D1A\u4E00\u500B\u9023\uFF0C\u7E23\u7D1A\u4E00\u500B\u6392\u7684\u898F\u6A21\uFF0C\u5404\u52D5\u54E1\u4E00\u652F\u4FE1\u606F\u901A\u8A0A\u529B\u91CF\u9032\u884C\u4FDD\u969C\u3002</p><p>1.6\u3001\u7701\u7D1A\u8ECD\u5730\u806F\u5408\u6307\u63EE\u90E8\u5DE5\u4F5C\u90E8\u7F72\u4FE1\u606F\u901A\u9053\uFF1A\u5229\u7528\u8ECD\u5730\u5404\u7D1A\u624B\u6BB5\uFF0C\u4E3B\u8981\u69CB\u5EFA\u56DB\u689D\u93C8\u8DEF\u3002</p><p>1.6.1\u3001\u8ECD\u59D4\u806F\u6307\u4E2D\u5FC3\u81F3\u6230\u5340\u806F\u6307\u4E2D\u5FC3\uFF0C\u81F3\u7701\u5E02\u7E23\u8ECD\u5730\u806F\u5408\u6307\u63EE\u6A5F\u69CB\u6307\u63EE\u4FE1\u606F\u93C8\u8DEF\uFF1B\u4E3B\u8981\u4F9D\u6258\u6230\u5340\u806F\u5408\u4F5C\u6230\u6307\u63EE\u4FE1\u606F\u7CFB\u7D71\u3001\u6A5F\u8981\u9060\u7A0B\u905E\u9001\u7CFB\u7D71\u3001\u878D\u5165\u806F\u5408\u4F5C\u6230\u9AD4\u7CFB\u3002</p><p>1.6.2\u3001\u8ECD\u59D4\u570B\u9632\u52D5\u54E1\u90E8\u81F3\u7701\u8ECD\u5340\u9818\u5C0E\u7BA1\u7406\u548C\u696D\u52D9\u6307\u5C0E\u4FE1\u606F\u93C8\u8DEF\uFF0C\u4E3B\u8981\u4F9D\u6258509+\u5E73\u81FA\uFF0C\u570B\u9632\u52D5\u54E1\u4EFB\u52D9\u4FE1\u606F\u6307\u63EE\u7CFB\u7D71\u8F14\u52A9\u52D5\u54E1\u7D44\u7E54\u6307\u63EE\u3002</p><p>1.6.3\u3001\u7701\u5E02\u7E23\u8ECD\u5730\u806F\u5408\u6307\u63EE\u6A5F\u69CB\u81F3\u4EFB\u52D9\u90E8\u5206\u968A\u6307\u63EE\u901A\u8A0A\u93C8\u8DEF\uFF0C\u4E3B\u8981\u4F9D\u6258\u8D85\u77ED\u6CE2\u3001\u5929\u901A\u4E00\u865F\u885B\u661F\u96FB\u8A71\uFF0C\u5EFA\u7ACB\u52D5\u4E2D\u901A\u6A5F\u52D5\u901A\u4FE1\u3002</p><p>1.6.4\u3001\u8ECD\u968A\u81F3\u5730\u65B9\u6307\u63EE\u901A\u8A0A\u93C8\u8DEF\uFF0C\u4F9D\u6258\u9D3B\u57FA\uFF08\u6DF1\u5733\u9F0E\u6613\u9D3B\u57FA\u79D1\u6280\u6709\u9650\u516C\u53F8\uFF09\u96FB\u5B50\u653F\u52D9\u5167\u7DB2\u6A5F\u8981\u5BC6\u78BC\u7CFB\u7D71\u76F8\u4E92\u901A\u806F\uFF0C\u5728\u5730\u65B9\u76F8\u95DC\u90E8\u9580\u5E03\u8A2D\u570B\u9632\u52D5\u54E1\u4FE1\u606F\u7CFB\u7D71\uFF0C\u5728\u7701\u8ECD\u5730\u806F\u5408\u6307\u63EE\u90E8\u5F15\u63A5\u5730\u65B9\u5404\u985E\u6578\u64DA\u4FE1\u606F\uFF0C\u6253\u901A\u8ECD\u5730\u901A\u806F\u93C8\u8DEF\u3002</p><p>2.\u5357\u6D77\u65B9\u5411\u6307\u63EE\u6240\u4EBA\u54E1\u7D44\u6210\u548C\u5DE5\u4F5C\u8077\u80FD\uFF1A</p><p>\u7531\u6307\u63EE\u90E8\u7CBE\u5E79\u529B\u91CF\u7DE8\u6210\uFF0C\u9069\u6642\u5411\u6E5B\u6C5F\u5730\u5340\u6D3E\u51FA\uFF0C\u91CD\u9EDE\u8CA0\u8CAC\u5357\u6D77\u65B9\u5411\u6D77\u4E0A\u53C3\u6230\u652F\u524D\u884C\u52D5\uFF0C\u7D44\u7E54\u7C4C\u5283\u8207\u6307\u63EE\u63A7\u88FD\u3002</p><p>3.\u5C08\u9805\u6307\u63EE\u6240\u884C\u696D\u69CB\u6210\u8207\u5DE5\u4F5C\u8077\u80FD\uFF1A</p><p>\u4F9D\u6258\u8ECD\u5730\u6709\u95DC\u90E8\u9580\u548C\u884C\u696D\u7CFB\u7D71\uFF0C\u7D44\u5EFA\u4EBA\u6C11\u9632\u7A7A\uFF0C\u4EA4\u901A\u52D5\u54E1\uFF0C\u570B\u6C11\u7D93\u6FDF\u52D5\u54E1\uFF0C\u8F3F\u8AD6\u5BA3\u50B3\uFF0C\u7DAD\u7A69\u6CBB\u4E82\u7B49\u5341\u4E8C\u500B\u5C08\u9805\u6307\u63EE\u6240\uFF0C\u70BA\u5404\u727D\u982D\u55AE\u4F4D\u958B\u8A2D\uFF0C\u5728\u7701\u8ECD\u5730\u806F\u5408\u6307\u63EE\u90E8\u7D71\u4E00\u6307\u63EE\u4E0B\uFF0C\u5206\u9818\u57DF\u7D44\u7E54\u843D\u5BE6\u52D5\u54E1\u4EFB\u52D9\uFF0C\u7D44\u7E54\u6307\u63EE\u5C08\u9805\u884C\u52D5\u3002</p><h2 id="\u7701\u7D1A\u8ECD\u5730\u6307\u63EE\u6A5F\u69CB\u7D44\u7E54\u67B6\u69CB\u7684\u5206\u6790" tabindex="-1"><a class="header-anchor" href="#\u7701\u7D1A\u8ECD\u5730\u6307\u63EE\u6A5F\u69CB\u7D44\u7E54\u67B6\u69CB\u7684\u5206\u6790" aria-hidden="true">#</a> \u7701\u7D1A\u8ECD\u5730\u6307\u63EE\u6A5F\u69CB\u7D44\u7E54\u67B6\u69CB\u7684\u5206\u6790</h2><p>\u4E8C\u3001\u7701\u7D1A\u8ECD\u5730\u6307\u63EE\u6A5F\u69CB\u7D44\u7E54\u67B6\u69CB\u7684\u5206\u6790</p><p>1\u3001\u7701\u7D1A\u8ECD\u5730\u806F\u5408\u6307\u63EE\u90E8\u5206\u6790</p><p>1.1\u3001\u5F9E\u806F\u5408\u6307\u63EE\u90E8\u7684\u7D44\u6210\u55AE\u4F4D\u770B\uFF0C\u662F\u7531\u4E2D\u570B\u7279\u6709\u7684\u9EE8\u59D4\u3001\u653F\u5E9C\u548C\u8ECD\u968A\u69CB\u6210\uFF0C\u5373\u6211\u5011\u5E38\u8AAA\u7684\u300C\u9EE8\u653F\u8ECD\u300D\uFF0C\u9644\u5C6C\u7684\u662F\u6B66\u8B66\u90E8\u968A\u548C\u6D77\u8B66\u90E8\u968A\uFF0C\u4E5F\u5C31\u662F\u4E2D\u570B\u8ECD\u8B66\u7279\u61B2\u7684\u4E00\u90E8\u5206\uFF0C\u53E6\u5916\uFF0C\u7701\u570B\u52D5\u59D4\u76F8\u95DC\u6210\u54E1\u4F01\u696D\u55AE\u4F4D\uFF0C\u662F\u4E2D\u570B\u592E\u4F01\u5728\u5EE3\u6771\u7684\u5206\u516C\u53F8\uFF0C\u6216\u8005\u662F\u540D\u70BA\u6C11\u4F01\uFF0C\u5BE6\u70BA\u4E2D\u5171\u8ECD\u6C11\u878D\u5408\u4F01\u696D\uFF0C\u6BD4\u5982\u83EF\u70BA\u2026\u2026</p><p>1.2\u3001\u5F9E\u806F\u5408\u6307\u63EE\u90E8\u7684\u5169\u500B\u4E2D\u5FC3\uFF08\u6307\u63EE\u63A7\u88FD\u4E2D\u5FC3\u8207\u570B\u9632\u52D5\u54E1\u4E2D\u5FC3\uFF09\u7684\u5DE5\u4F5C\u8077\u80FD\u4E0A\u770B\uFF0C\u9019\u500B\u6307\u63EE\u90E8\u9084\u4E0D\u662F\u524D\u7AEF\u90E8\u7F72\u548C\u8ECD\u4E8B\u9032\u653B\u7684\u6307\u63EE\u90E8\uFF0C\u800C\u662F\u5F8C\u65B9\u7684\u8ECD\u4E8B\u9632\u885B\u3001\u5F8C\u52E4\u4FDD\u969C\u3001\u652F\u63F4\u524D\u7DDA\u3001\u7D44\u7E54\u52D5\u54E1\u548C\u7DAD\u6301\u7A69\u5B9A\u7684\u7701\u7D1A\u8ECD\u5730\u806F\u5408\u6307\u63EE\u90E8\u3002</p><p>1.3\u3001\u5F9E\u806F\u5408\u6307\u63EE\u90E8\u7684\u4EBA\u54E1\u69CB\u6210\u4E0A\u770B\uFF0C\u7701\u59D4\u66F8\u8A18\u70BA\u7B2C\u4E00\u8CA0\u8CAC\u4EBA\uFF0C\u5176\u4ED6\u4EBA\u54E1\u70BA\u7701\u9577\u548C\u7701\u8ECD\u5340\u53F8\u4EE4\u54E1\uFF0C\u7B26\u5408\u4E2D\u570B\u9EE8\u653F\u8ECD\u7684\u6CBB\u7406\u7BA1\u63A7\u6A21\u5F0F\u3002</p><p>1.4\u3001\u5F9E\u806F\u5408\u6307\u63EE\u90E8\u7684\u5206\u5DE5\u4E0A\u770B\uFF0C\u7701\u59D4\u66F8\u8A18\u8CA0\u8CAC\u6307\u63EE\u63A7\u88FD\u4E2D\u5FC3\u548C\u570B\u9632\u52D5\u54E1\u4E2D\u5FC3\u7684\u5168\u9762\u5DE5\u4F5C\uFF1B\u7701\u9577\u8CA0\u8CAC\u570B\u9632\u52D5\u54E1\u4E2D\u5FC3\u7684\u5DE5\u4F5C\uFF0C\u7D44\u7E54\u5168\u7701\u7684\u6230\u722D\u52D5\u54E1\u4EFB\u52D9\u5BE6\u65BD\uFF1B\u7701\u8ECD\u5340\u53F8\u4EE4\u54E1\u8CA0\u8CAC\u6307\u63EE\u63A7\u88FD\u4E2D\u5FC3\u7684\u5DE5\u4F5C\uFF0C\u7D44\u7E54\u53C3\u6230\u652F\u524D\u884C\u52D5\uFF0C\u5354\u540C\u7701\u9577\u52D5\u54E1\u4EFB\u52D9\u843D\u5BE6\uFF0C\u7701\u8ECD\u5340\u653F\u6CBB\u59D4\u54E1\u505A\u597D\u653F\u6CBB\u5DE5\u4F5C\uFF0C\u5354\u540C\u53F8\u4EE4\u54E1\u6307\u63EE\u76F8\u95DC\u884C\u52D5\u3002</p><p>1.5\u3001\u5F9E\u806F\u5408\u6307\u63EE\u90E8\u7684\u4FDD\u969C\u9AD4\u7CFB\u4E0A\u770B\uFF0C\u5728\u4F9D\u6258\u6578\u64DA\u4FE1\u606F\u5BA4\u4FE1\u606F\u4FDD\u969C\u7684\u57FA\u790E\u4E0A\uFF0C\u6309\u7167\u7701\u7D1A\u4E00\u500B\u71DF\uFF0C\u5E02\u7D1A\u4E00\u500B\u9023\uFF0C\u7E23\u7D1A\u4E00\u500B\u6392\u7684\u898F\u6A21\uFF0C\u5404\u52D5\u54E1\u4E00\u652F\u4FE1\u606F\u901A\u8A0A\u529B\u91CF\u9032\u884C\u4FDD\u969C\u3002\u9019\u88CF\u8AAA\u660E\u806F\u5408\u6307\u63EE\u90E8\u9664\u4E86\u7701\u7D1A\u4EE5\u5916\uFF0C\u5411\u4E0B\u9084\u6709\u5730\u5E02\u7D1A\u3001\u7E23\u5340\u7D1A\u7684\u8ECD\u5730\u806F\u5408\u6307\u63EE\u90E8\u3002</p><p>1.6\u3001\u5F9E\u806F\u5408\u6307\u63EE\u90E8\u5DE5\u4F5C\u50B3\u905E\u4E0B\u9054\u7684\u4FE1\u606F\u8DEF\u5F91\u4E0A\u770B\uFF0C\u8ECD\u59D4\u806F\u5408\u6307\u63EE\u4E2D\u5FC3\u8207\u7701\u7D1A\u3001\u5E02\u7D1A\u3001\u7E23\u7D1A\u8ECD\u5730\u806F\u5408\u6307\u63EE\u90E8\u7684\u4FE1\u606F\u6E9D\u901A\u4F9D\u9760\u8ECD\u65B9\u7684\u806F\u5408\u6307\u63EE\u4FE1\u606F\u7CFB\u7D71\u3001\u6A5F\u8981\u9060\u7A0B\u905E\u9001\u7CFB\u7D71\uFF1B\u8ECD\u59D4\u570B\u9632\u52D5\u54E1\u8207\u7701\u8ECD\u5340\u570B\u9632\u52D5\u54E1\u7684\u4FE1\u606F\u6E9D\u901A\u4E3B\u8981\u4F9D\u9760509+\u5E73\u81FA\uFF0C\u570B\u9632\u52D5\u54E1\u4EFB\u52D9\u4FE1\u606F\u6307\u63EE\u7CFB\u7D71\u70BA\u8F14\uFF1B\u7701\u5E02\u7E23\u8ECD\u5730\u806F\u5408\u6307\u63EE\u8207\u5404\u57F7\u884C\u4EFB\u52D9\u90E8\u9580\u4FE1\u606F\u6E9D\u901A\u4F9D\u9760\u8D85\u77ED\u6CE2\u3001\u5929\u901A\u4E00\u865F\u885B\u661F\u96FB\u8A71\uFF1B\u4F5C\u6230\u90E8\u968A\u8207\u5730\u65B9\u806F\u5408\u6307\u63EE\u90E8\u7684\u4FE1\u606F\u806F\u7D61\u4F9D\u6258\uFF08\u6DF1\u5733\u9F0E\u6613\u9D3B\u57FA\u79D1\u6280\u6709\u9650\u516C\u53F8\u958B\u767C\u7684\uFF09\u9D3B\u57FA\u96FB\u5B50\u653F\u52D9\u5167\u7DB2\u6A5F\u8981\u5BC6\u78BC\u7CFB\u7D71\uFF0C\u5730\u65B9\u76F8\u95DC\u53C3\u8207\u90E8\u9580\u958B\u901A\u570B\u9632\u52D5\u54E1\u4FE1\u606F\u7CFB\u7D71\uFF0C\u7701\u8ECD\u5730\u806F\u5408\u6307\u63EE\u90E8\u63A5\u5165\u5730\u65B9\u5404\u985E\u6578\u64DA\u4FE1\u606F\uFF0C\u5BE6\u73FE\u8ECD\u5730\u4FE1\u606F\u4E92\u4EAB\u548C\u5C0D\u63A5\u3002</p><p>2\u3001\u5F9E\u5357\u6D77\u65B9\u5411\u6307\u63EE\u6240\u7684\u4EBA\u54E1\u69CB\u6210\u548C\u5DE5\u4F5C\u8077\u80FD\u4E0A\u770B\uFF0C\u9019\u662F\u4E00\u500B\u9762\u5411\u5357\u6D77\u65B9\u5411\u7684\u6D77\u4E0A\u5099\u6230\u52D5\u54E1\u3001\u53C3\u6230\u652F\u524D\u7684\u57F7\u884C\u5BE6\u65BD\u90E8\u9580\u3002</p><p>3\u3001\u5F9E\u5341\u4E8C\u500B\u5C08\u9805\u6307\u63EE\u6240\u7684\u884C\u696D\u69CB\u6210\u548C\u5DE5\u4F5C\u8077\u80FD\u4E0A\u770B\uFF0C\u9019\u4E9B\u5C08\u9805\u6307\u63EE\u6240\u662F\u5BE6\u884C\u300C\u4EBA\u6C11\u6230\u722D\u300D\uFF0C\u8ABF\u52D5\u4E00\u5207\u8CC7\u6E90\u70BA\u4E2D\u5171\u6240\u7528\uFF0C\u63A8\u9032\u8D85\u9650\u6230\u722D\uFF0C\u570B\u9632\u52D5\u54E1\u5099\u6230\uFF0C\u53C3\u6230\u652F\u524D\u7684\u57F7\u884C\u548C\u5BE6\u65BD\u90E8\u9580\u3002</p><h2 id="\u7E3D\u7D50\u8207\u5EFA\u8B70" tabindex="-1"><a class="header-anchor" href="#\u7E3D\u7D50\u8207\u5EFA\u8B70" aria-hidden="true">#</a> \u7E3D\u7D50\u8207\u5EFA\u8B70</h2><p>\u4E09\u3001\u7E3D\u7D50\u8207\u5EFA\u8B70</p><p>1\u3001\u7E3D\u7D50</p><p>1.1\u3001\u8ECD\u5730\u806F\u5408\u6307\u63EE\u90E8\u662F\u9019\u500B\u532F\u5831\u8B1B\u8A71\u7684\u91CD\u9EDE\u5167\u5BB9\uFF0C\u8ECD\u5730\u806F\u5408\u6307\u63EE\u90E8\u7684\u8077\u80FD\u662F\u5F8C\u65B9\u7684\u8ECD\u4E8B\u9632\u885B\u3001\u5F8C\u52E4\u4FDD\u969C\u3001\u652F\u63F4\u524D\u7DDA\u3001\u7D44\u7E54\u52D5\u54E1\u548C\u7DAD\u6301\u7A69\u5B9A\u7684\u9EE8\u653F\u8ECD\u806F\u5408\u6307\u63EE\u4E2D\u5FC3\u3002</p><p>1.2\u3001\u4E2D\u5171\u4E2D\u592E\u8ECD\u59D4\u548C\u570B\u9632\u52D5\u54E1\u59D4\u54E1\u6703\u7D44\u6210\u7684\u4E2D\u592E\u8ECD\u59D4\u806F\u5408\u6307\u63EE\u4E2D\u5FC3\u662F\u4E2D\u570B\u8ECD\u5730\u806F\u5408\u6307\u63EE\u7684\u6C7A\u7B56\u90E8\u9580\u3002</p><p>1.3\u3001\u5404\u7701\u7D1A\u8ECD\u5730\u8ECD\u5730\u806F\u5408\u6307\u63EE\u90E8\u662F\u4E2D\u570B\u8ECD\u5730\u806F\u5408\u6307\u63EE\u7684\u7701\u7D1A\u57F7\u884C\u90E8\u9580\uFF0C\u5C0D\u61C9\u7684\u4E0B\u7D1A\u57F7\u884C\u90E8\u9580\u662F\u5730\u5E02\u7D1A\u3001\u7E23\u5340\u7D1A\u7684\u8ECD\u5730\u806F\u5408\u6307\u63EE\u90E8\u3002</p><p>1.4\u3001\u7701\u7D1A\u65B9\u5411\u6307\u63EE\u6240\u548C\u5404\u5C08\u9805\u6307\u63EE\u6240\u662F\u57F7\u884C\u90E8\u9580\u4E4B\u4E0B\u7684\u5177\u9AD4\u843D\u5BE6\u90E8\u9580\uFF0C\u7701\u7D1A\u6307\u63EE\u6240\u5C0D\u61C9\u7684\u4E0B\u7D1A\u5BE6\u65BD\u90E8\u9580\u6703\u6709\u5730\u5E02\u7D1A\u3001\u7E23\u5340\u7D1A\uFF0C\u7E23\u5340\u7D1A\u5EF6\u4F38\u5230\u8857\u9053\u7D1A\uFF0C\u8857\u9053\u7D1A\u76F4\u63A5\u5230\u7DB2\u683C\u5316\u7BA1\u63A7\u3002</p><p>2\u3001\u5EFA\u8B70</p><p>2.1\u3001\u6316\u6398\u570B\u9632\u52D5\u54E1\u59D4\u54E1\u6703\u653F\u5E9C\u7D44\u6210\u90E8\u9580\u8A73\u7D30\u540D\u55AE\uFF0C\u4EE5\u53CA\u5EE3\u6771\u7701\u570B\u9632\u52D5\u54E1\u59D4\u54E1\u6703\u653F\u5E9C\u53C3\u8207\u90E8\u9580\u6E05\u55AE\u3002</p><p>2.2\u3001\u6316\u6398\u53C3\u8207\u570B\u9632\u52D5\u54E1\u59D4\u54E1\u6703\u570B\u8CC7\u59D4\u592E\u4F01\u8207\u5EE3\u6771\u7701\u7D1A\u570B\u8CC7\u59D4\u7701\u4F01\u7684\u8A73\u7D30\u540D\u55AE</p><p>2.3\u3001\u6316\u6398\u53C3\u8207\u570B\u9632\u52D5\u54E1\u59D4\u54E1\u6703\u96B1\u5F62\u7684\u8ECD\u6C11\u878D\u5408\u4F01\u696D\uFF0C\u5C24\u5176\u662F\u7576\u5730\u7684\u5EE3\u6771\u7701\u5730\u65B9\u8ECD\u6C11\u878D\u5408\u7684\u96B1\u5F62\u4F01\u696D\u3002</p><p>2.4\u3001\u8A73\u7D30\u4E86\u89E3\u4E0A\u8FF0\u53C3\u8207\u5099\u6230\u52D5\u54E1\u3001\u53C3\u6230\u652F\u524D\u5404\u55AE\u4F4D\u7684\u4E3B\u8981\u9818\u5C0E\u8207\u8CA0\u8CAC\u4EBA\u7684\u60C5\u6CC1</p><p>2.5\u3001\u7D44\u7E54\u4E86\u89E3\u548C\u6316\u6398\u8ECD\u5730\u806F\u5408\u6307\u63EE\u6A5F\u69CB\u4FE1\u606F\u806F\u7D61\u4F9D\u6258\u7684\u901A\u4FE1\u7CFB\u7D71\u53CA\u5DE5\u7A0B\uFF0C\u9084\u6709\u4ED6\u5011\u7684\u4FE1\u606F\u4E2D\u5FC3\u3001\u5099\u4EFD\u4E2D\u5FC3\u3001\u885B\u661F\u93C8\u8DEF\u3001\u885B\u661F\u4E2D\u7E7C\u7AD9\u3001\u79DF\u7528\u4E92\u806F\u7DB2\u5C08\u7DDA\u3001\u5099\u7528\u4E92\u806F\u7DB2\u5C08\u7DDA\u7B49\u8A73\u7D30\u4FE1\u606F\u3002</p>',55),d=[h];function i(c,n){return e(),a("div",null,d)}var l=p(t,[["render",i],["__file","013.html.vue"]]);export{l as default};
